import Maybe from 'graphql-yoga/node_modules/graphql/tsutils/Maybe';
import { ASTVisitor } from 'graphql-yoga/node_modules/graphql/language/visitor';
import { ValidationContext } from 'graphql-yoga/node_modules/graphql/validation/ValidationContext';

export function undefinedVarMessage(
  varName: string,
  opName: Maybe<string>,
): string;

/**
 * No undefined variables
 *
 * A GraphQL operation is only valid if all variables encountered, both directly
 * and via fragment spreads, are defined by that operation.
 */
export function NoUndefinedVariables(context: ValidationContext): ASTVisitor;
